import React, { Component } from 'react';
import {
  Link
} from "react-router-dom";

class CreateCaptionCard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 'Write a funny caption here'
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }
  render() {
    return (
      <div className="CaptionActionsMainContainer">
        <div
          className="CaptionActionsButton CaptionActionsButtonBottomMargin"
          >
          <Link style={{display: 'block', height: '100%'}} to='/create-card'>Create Caption Card</Link>
        </div>
      </div>
    );
  }
}

export default CreateCaptionCard;
