import React, { Component } from 'react';
import Timer from "react-compound-timer";
import './Meme.css';

class CustomTimer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeInterval: this.props.timeInterval, // in seconds
      timeRemaing: 0 // time to display
    };
  }

  render() {
    return (
      <Timer
        initialTime={this.state.timeInterval}
        direction="backward"
        >
        {() => (
            <h2>
                <Timer.Hours /> :
                <Timer.Minutes /> :
                <Timer.Seconds /> 
            </h2>
        )}
</Timer>
    );
  }
}

export default CustomTimer;
