import React, { Component } from 'react';
import Timer from './Timer';
import './Meme.css';

const ROUND_TIME = 1.8 * Math.pow(10,7);

class Meme extends Component {

  constructor(props) {
    super(props);
    this.state = {
      didFetchImage: false
    };
  }

  componentDidMount() {
    this.fetchImage()
  }

  fetchImage() {
    // fetch('/api/getMeme')
    // .then((res) => res.arrayBuffer())
    // .then(imageBuffer => console.log(imageBuffer));
    fetch('/api/getMeme')
    .then((res) => res.text())
    .then(imageName => {
      this.setState({
        didFetchImage: true,
        imageName: imageName
      });
    });
  }

  startNewRound() {
    console.log("new round");
  }

  render() {
    return (
      <div className="MemeContainer">
        <div className="MemePhoto" style={ { backgroundImage: `url(require("images/img.svg"))` } }/>
        <h5 className="TimeCaption"> Time Remaining in Round </h5>
        <Timer timeInterval={ROUND_TIME}/>
      </div>
    );
  }
}

export default Meme;
