import React, { Component } from 'react';
import Timer from './Timer';
import './Meme.css';

class Meme extends Component {

  constructor(props) {
    super(props);
    this.state = {
      didFetchImage: false,
      remainingTime: 0
    };
  }

  componentDidMount() {
    this.fetchImage();
  }

  fetchImage() {
    fetch('/api/getMeme')
    .then((res) => res.json())
    .then(data => {
      this.setState({
        didFetchImage: true,
        imageName: data.image,
        remainingTime: data.timestamp
      });
    });
  }

  startNewRound() {
    console.log("new round");
  }

  render() {
    console.log(this.state.remainingTime)
    return (
      <div className="MemeContainer">
        <img className="MemePhoto" src={this.state.imageName}/>

        <h5 className="TimeCaption"> Time Remaining in Round </h5>
        <Timer timeInterval={this.state.remainingTime}/>
      </div>
    );
  }
}

export default Meme;
