import React, { Component } from 'react';
import Timer from "react-compound-timer";
import './Meme.css';


const withTimer = timerProps => WrappedComponent => wrappedComponentProps => (
  <Timer {...timerProps}>
    {timerRenderProps =>
      <WrappedComponent {...wrappedComponentProps} timer={timerRenderProps} />}
  </Timer>
);

class CustomTimer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeInterval: this.props.timeInterval, // in milliseconds
    };
  }

  restart = () => {
   console.log('My own function');
   this.forceUpdate()
 }

componentDidMount() {
      const { setCheckpoints, setDirection, setTime, start } = this.props.timer;

      setCheckpoints([
          {
              time: 1000,
              callback: () => setDirection('forward'),
          },
          {
              time: 5000,
              callback: () => setDirection('backward'),
          },
      ]);

      setTimeout(() => {
          setTime(10000);
          start();
      }, 3000);
  }

  render() {
    return (
      <Timer
        initialTime={this.state.timeInterval}
        direction="backward"
        checkpoints={[
        {
            time: 0,
            callback: () => {},
        }
        ]}
        >
        {({reset}) => (
          <div>
            <h3 className="TimerCountdown">
                <Timer.Hours />:
                <Timer.Minutes />:
                <Timer.Seconds />
            </h3>


                <button onClick={this.props.reset}>Reset</button>
            </div>
        )}
        </Timer>
    );
  }
}

export default CustomTimer;
