import React, { Component } from 'react';
import {Navbar} from 'react-bootstrap';
import Home from "./Home";
import CreateCaptionCard from "./CreateCaptionCard";
import {
  Link, Switch, BrowserRouter as Router, Route
} from "react-router-dom";
import './App.css';
import web3 from './web3'
const account = "0xCd626bc764E1d553e0D75a42f5c4156B91a63F23";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      hasMetamask: false,
      isOnRopsten: false
    }
    this.isMetamaskInstalled()
  }

  onBuy = (id) => {
    alert('Buying ' + id);
  }

  onSell = (id) => {
    alert('Selling ' + id);
  }

  getBalance() {
    web3.eth.getBalance(account, (err, wei) => {
      console.log(web3.utils.fromWei(wei, 'ether'));
    });
  }

  async isMetamaskInstalled() {
    if (typeof web3 !== 'undefined'){
      var isLoggedIn = await this.isMetamaskLoggedIn();
      var networkId = await web3.eth.net.getId();
      this.setState({
        hasMetamask: true,
        isLoggedIn: isLoggedIn,
        isOnRopsten: networkId === 3
      });
      console.log("here")
      console.log(await isLoggedIn)
   }
   else{
      this.setState({hasMetamask: false});
   }
  }

  isMetamaskLoggedIn() {
    web3.eth.getAccounts(function(err, accounts){
      if (err != null) {
         return false;
      }
      else if (accounts.length === 0) {
         return false;
      }
      else {
         return true;
      }
   });
  }

  render() {
    this.isMetamaskLoggedIn()
    return (
      <Router>
          <Navbar className="nav" variant="dark" sticky="top">
            <Navbar.Brand as={Link} to="/">
              <span className="blueText">#WhatDo</span>
              <span className="purpleText">You</span>
              <span className="pinkText">CryptoMeme? </span>
            </Navbar.Brand>
          </Navbar>
          {this.state.hasMetamask && this.state.isLoggedIn
            ?
            <Switch>
              <Route path="/create-card/" component={CreateCaptionCard}/>
              <Route path="/*" component={Home}/>
            </Switch>
            :
            null }
      </Router>
    );
  }
}

export default App;
