import React, { Component } from 'react';
import CaptionView from './CaptionView';
import CaptionListHeader from './CaptionListHeader';
import './CaptionList.css';
import CaptionCardFactory from '../web3Contracts/CaptionCardFactory';
import CaptionCard from '../web3Contracts/CaptionCard';

// TODO: actually fetch and fill with real data
import EXAMPLE_DATA from '../example_data.json'

class CaptionList extends Component {

  constructor(props) {
    super(props);
    this.data = EXAMPLE_DATA;
  }

  onBuy = (id) => {
    alert('Buying ' + id);
  }

  onSell = (id) => {
    alert('Selling ' + id);
  }

  async getCaptionCards() {
    const addr = await CaptionCardFactory.methods.captionCards(0).call();
    CaptionCard.options.address = addr;
    let caption = await CaptionCard.methods.captionText().call();
    this.setState({
      data: {id: "1", price: "0", balance: 1, caption: caption}
    });
    console.log(this.data);
  }

  render() {
    this.getCaptionCards()
    const captions = this.state.data.map((data) => {
      return <CaptionView
        id={data.id}
        key={data.id}
        caption={data.caption}
        balance={data.balance}
        price={data.price}
        onSell={this.onSell}
        onBuy={this.onBuy}
      />
    });

    return (
      <div className="CaptionList">
        <CaptionListHeader />
        <div className="CaptionListMainContainer">
          {captions}
        </div>
      </div>
    );
  }
}

export default CaptionList;
