import React, { Component } from 'react';
import Timer from "react-compound-timer";
import './Meme.css';

const withTimer = timerProps => WrappedComponent => wrappedComponentProps => (
  <Timer {...timerProps}>
    {timerRenderProps =>
      <WrappedComponent {...wrappedComponentProps} timer={timerRenderProps} />}
  </Timer>
);


class CustomTimer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeInterval: this.props.timeInterval, // in milliseconds
    };
  }

  restart = () => {
   console.log('My own function');
   this.forceUpdate()
 }


  render() {
    return (
      <Timer
        initialTime={this.state.timeInterval}
        direction="backward"
        checkpoints={[
        {
            time: 0,
            callback: () => {},
        }
        ]}
        >
            <h3 className="TimerCountdown">
                <Timer.Hours />:
                <Timer.Minutes />:
                <Timer.Seconds />
            </h3>
        </Timer>
    );
  }
}

const TimerHOC = withTimer({
    direction: 'backward',
    initialTime: 5000,
    startImmediately: false,
})(CustomTimer);

export default TimerHOC;
