import React, { Component } from 'react';
import Timer from "react-compound-timer";
import './Meme.css';

const ROUND_TIME = 1.8 * Math.pow(10,4);


const withTimer = timerProps => WrappedComponent => wrappedComponentProps => (
  <Timer {...timerProps}>
  {timerRenderProps =>
    <WrappedComponent {...wrappedComponentProps} timer={timerRenderProps} />}
    </Timer>
  );


class CustomTimer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeInterval: this.props.timeInterval, // in milliseconds
    };
  }

  componentDidUpdate() {
    const { setTime, getTime, reset} = this.props.timer;
    console.log("here" + this.props.timeInterval)
    if (getTime() == ROUND_TIME) {
      reset()
    }
  }

  componentDidMount() {
    const { setCheckpoints, setTime, getTime} = this.props.timer;
    setTime(this.state.timeInterval);
    setCheckpoints([
      {
        time: 0,
        callback: () => {
          this.props.startNewRound();
          setTime(ROUND_TIME);
        }
      }
    ]);
  }

  render() {
    return (
      <h3 className="TimerCountdown">
        <Timer.Hours />:
        <Timer.Minutes />:
        <Timer.Seconds />
      </h3>
    );
  }
}

  const TimerHOC = withTimer({
    direction: 'backward',
    initialTime: 0,
  })(CustomTimer);

  export default TimerHOC;
